import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Checklist = () => {
  const [checklist, setChecklist] = useState([]);

  useEffect(() => {
    fetch('/api/checklist')
      .then(response => response.json())
      .then(data => setChecklist(data));
  }, []);

  const handleCheckboxChange = (item) => {
    const updatedChecklist = checklist.filter(i => i !== item);
    setChecklist(updatedChecklist);
    axios.post('/api/update-checklist', { checklist: updatedChecklist })
      .then(response => {
        // Handle success if necessary
      })
      .catch(error => {
        // Handle error if necessary
      });
  };

  return (
    <div>
      {checklist.map(item => (
        <div key={item}>
          <input type="checkbox" defaultChecked onChange={() => handleCheckboxChange(item)} />
          <label>{item}</label>
        </div>
      ))}
    </div>
  );
};

export default Checklist;
