import React, { useEffect, useState } from "react";
import logo from './logo.svg';
import './App.css';

function App() {
  const [response, setResponse] = useState('');
  const [result, setResult] = useState('');
  const [currentTime, setCurrentTime] = useState(0);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    alert(`The response was: ${response}`);

    try {
      const app_response = await fetch('/api/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
        body: JSON.stringify({ response }),
      });

      if (app_response.ok) {
        const reply = await response;
        setResult(reply);
      } else {
        throw new Error('Request failed with status ' + app_response.status);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <p>The current time is {currentTime}.</p>
      <form onSubmit={handleSubmit}>
        <input type="text" value={response} onChange={(e) => setResponse(e.target.value)} />
        <button type="submit">Submit</button>
      </form>
      {result && <div>{JSON.stringify(result)}</div>}
    </div>
  );
}

export default App;
