import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Checklist = () => {
  const [checklist, setChecklist] = useState([]);
  const [checkedItems, setCheckedItems] = useState({});

  useEffect(() => {
    fetch('/api/checklist')
      .then(response => response.json())
      .then(data => {
        setChecklist(data);
        const initialCheckedItems = {};
        data.forEach(item => {
          initialCheckedItems[item] = true; // Assume all items are initially checked
        });
        setCheckedItems(initialCheckedItems);
      });
  }, []);

  const handleCheckboxChange = (item) => {
    const updatedCheckedItems = { ...checkedItems };
    updatedCheckedItems[item] = !checkedItems[item];
    setCheckedItems(updatedCheckedItems);

    const updatedChecklist = Object.keys(updatedCheckedItems).filter(key => updatedCheckedItems[key]);
    setChecklist(updatedChecklist);

    axios.post('/api/update-checklist', { checklist: updatedChecklist })
      .then(response => {
        // Handle success if necessary
      })
      .catch(error => {
        // Handle error if necessary
      });
  };

  return (
    <div>
      {checklist.map(item => (
        <div key={item}>
          <input
            type="checkbox"
            checked={checkedItems[item]}
            onChange={() => handleCheckboxChange(item)}
          />
          <label>{item}</label>
        </div>
      ))}
    </div>
  );
};

export default Checklist;
