import React, { useEffect, useState } from "react";
import logo from './logo.svg';
import './App.css';
import './form.css';
import Checklist from './Checklist';
import Form from './features/form/Form';
import { connect, useDispatch } from 'react-redux';

const handleGoBack = (_) => {
  dispatch({
    type: 'submit',
    payload: {
        user_prompt: prompt,
        mode: mode,
        num_songs: numSongs,
        title: title,
        input_error: inputError
    }
  })
  // setResult('');
  // setPrompt('');
  // setNumSongs('');
  // setTitle('');
  // setInputError(null);
}

const mapStateToProps = (state) => {
  return {
    result: state.form.mode, 
    user_prompt: state.form.user_prompt, 
    num_songs: state.form.num_songs, 
    title: state.form.title, 
    input_error: state.form.input_error, 
  };
};

function App(props) {
  const { result } = props.result;
  return (
    <div class="html">
       {/* Form */}
      {!result && <Form/>}

      {/* Result */}
      <div class="playlist">
      {/* {result && <Playlist prompt={prompt} result = {result}/>} */}
      {result && <Checklist/>}
      <br/>
      { result && <button onClick={handleGoBack}>
        Go Back
      </button>
      }
      </div>
    </div>
  );
}

export default connect(mapStateToProps)(App);
