import React, { useEffect, useState } from "react";
import logo from './logo.svg';
import './App.css';

function App() {
  const [response, setResponse] = useState('');
  const [result, setResult] = useState('');
  const [currentTime, setCurrentTime] = useState(0);
  const [numSongs, setNumSongs] = useState(0);
  const [title, setTitle] = useState('');

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  const handleNumSongsChange = (event) => {
    setNumSongs(event.target.value);
  };
  const handleResponseChange = (event) => {
    setResponse(event.target.value);
  };
  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const app_response = await fetch('/api/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
        body: JSON.stringify({ "response": response, "num_songs": numSongs, "title": title}),
      });

      if (app_response.ok) {
        const app_response_json = app_response.json();
        app_response_json.then(data => setResult(data["playlist"]));
      } else {
        throw new Error('Request failed with status ' + app_response.status);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div class="App">
      <form class="App-header"  onSubmit={handleSubmit}>
        Playlist title:   
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        Description:   
        <input type="text" value={response} onChange={handleResponseChange} />
        Number of songs
        <input type="number" step="1" value = {numSongs} onChange={handleNumSongsChange} /> 
        {/* onChange={(e) => setNumSongs(e.target.value)}         */}
        <button type="submit">Submit</button>
      </form>
      <div class="App-playlist">
      {result && <div class="white-space-pre-line"> <div class="App-playlist-header">Playlist for <span class="App-prompt">{response}:</span> </div> <br/> {result} </div>}
      </div>
    </div>
  );
}

export default App;
