}

const getMode = (state) => {
  return {
    mode: state.form.mode, // Replace 'specificReducer' with the actual reducer name and 'data' with the specific data field you want to access
  };
};

const result = useSelector((state) => state.form.mode)

function App() {
  return (
    <div class="html">
       {/* Form */}
      {!result && <Form/>}

      {/* Result */}
      <div class="playlist">
      {/* {result && <Playlist prompt={prompt} result = {result}/>} */}
      {result && <Checklist/>}
      <br/>
      { result && <button onClick={handleGoBack}>
        Go Back
      </button>
      }
      </div>
    </div>
  );
}

export default App;
