import React, { useEffect, useState } from "react";
import logo from './logo.svg';
import './App.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [result, setResult] = useState('');
  const [currentTime, setCurrentTime] = useState(0);
  const [numSongs, setNumSongs] = useState(0);
  const [title, setTitle] = useState('');
  const [inputError, setInputError] = useState(null);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  const handleNumSongsChange = (event) => {
    setNumSongs(event.target.value);
  };
  const handlePromptChange = (event) => {
    setPrompt(event.target.value);
  };
  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (prompt.length <= 0){
      setInputError('Please provide a playlist description!');
    }
    else if (numSongs <= 0){
      setInputError('Please give a number of songs!');
    }
    try {
      const app_response = await fetch('/api/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
        body: JSON.stringify({ "prompt": prompt, "num_songs": numSongs, "title": title}),
      });

      if (app_response.ok) {
        const app_response_json = app_response.json();
        app_response_json.then(data => setResult(data["playlist"]));
      } else {
        throw new Error('Request failed with status ' + app_response.status);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div class="App">
      <form class="App-header"  onSubmit={handleSubmit}>
        <label htmlFor="title">Playlist title:</label>
        <input type="text" value={title} onChange={handleTitleChange} />
        <label htmlFor="description">Description:</label>
        <input type="text" value={prompt} onChange={handlePromptChange} />
        <label htmlFor="num_songs">Number of songs:</label>
        <input type="number" step="1" value = {numSongs} onChange={handleNumSongsChange} /> 
        {inputError && <div style={{ color: 'red' }}>{inputError}</div>}
        <button type="submit">Submit</button>
      </form>
      <div class="App-playlist">
      {result && <div class="white-space-pre-line"> <div class="App-playlist-header">Playlist for <span class="App-prompt">{prompt}:</span> </div> <br/> {result} </div>}
      </div>
    </div>
  );
}

export default App;
