import React, { useEffect, useState } from "react";
import logo from './logo.svg';
import './App.css';
import './form.css';
import Checklist from './features/confirmation/Checklist';
import Form from './features/form/Form';
import { connect, useDispatch } from 'react-redux';
import store from './app/store';

const handleGoBack = (_) => {
  store.dispatch({
    type: 'go_back',
  });
  // setResult('');
  // setPrompt('');
  // setNumSongs('');
  // setTitle('');
  // setInputError(null);
}

const mapStateToProps = (state) => {
  return {
    result: state.form.mode, 
    user_prompt: state.form.user_prompt, 
    num_songs: state.form.num_songs, 
    title: state.form.title, 
    input_error: state.form.input_error, 
  };
};

function App(props) {
  //const result = props.result;
  return (
    <div class="html">
       {/* Form */}
      {!props.result && <Form/>}

      {/* Result */}
      <div class="playlist">
      {/* {result && <Playlist prompt={prompt} result = {result}/>} */}
      {props.result && <Checklist/>}
      {props.result==1 && <p>TEST</p>}

      <br/>
      { props.result && <button onClick={handleGoBack}>
        Go Back
      </button>
      }
      </div>
    </div>
  );
}

export default connect(mapStateToProps)(App);
