import React, { useEffect, useState } from "react";
import logo from './logo.svg';
import './App.css';
import './form.css';

function App() {
  const [prompt, setPrompt] = useState('');
  const [result, setResult] = useState('');
  const [currentTime, setCurrentTime] = useState(0);
  const [numSongs, setNumSongs] = useState(0);
  const [title, setTitle] = useState('');
  const [inputError, setInputError] = useState(null);

  useEffect(() => {
    fetch('/api/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);

  const handleNumSongsChange = (event) => {
    setNumSongs(event.target.value);
  };
  const handlePromptChange = (event) => {
    setPrompt(event.target.value);
  };
  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (prompt.length <= 0){
      setInputError('Please provide a playlist description!');
      return
    }
    else if (numSongs <= 0){
      setInputError('Please request a number of songs greater than 0!');
      return
    }
    else{
      setInputError(null);
    }
    try {
      const app_response = await fetch('/api/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*',
        },
        body: JSON.stringify({ "prompt": prompt, "num_songs": numSongs, "title": title}),
      });

      if (app_response.ok) {
        const app_response_json = app_response.json();
        app_response_json.then(data => setResult(data["playlist"]));
      } else {
        throw new Error('Request failed with status ' + app_response.status);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div class="App">
      <div class="container">
      <div class="cta-form">
      <h2>Create a new playlist!</h2> 
      <p>Submit the for for an AI generated playlist from your liked songs.</p>
      </div>
        <form class="form"  onSubmit={handleSubmit}>
          <label htmlFor="title">Playlist title:</label>
          <input class="form__input" type="text" value={title} onChange={handleTitleChange} />
          <label htmlFor="description">Description:</label>
          <input class="form__input" type="text" value={prompt} onChange={handlePromptChange} />
          <label htmlFor="num_songs">Number of songs:</label>
          <input class="form__input" type="number" step="1" value = {numSongs} onChange={handleNumSongsChange} /> 
          {inputError && <div style={{ color: 'red' }}>{inputError}</div>}
          <button type="submit">Submit</button>
        </form>
      </div>
      <div class="App-playlist">
      {result && <div class="white-space-pre-line"> <div class="App-playlist-header">Playlist for <span class="App-prompt">{prompt}:</span> </div> <br/> {result} </div>}
      </div>
    </div>
  );
}

export default App;
