import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [response, setResponse] = useState('');
  const [result, setResult] = useState('');
  let headers = new Headers();

  headers.append('Content-Type', 'application/json');
  headers.append('Accept', 'application/json');
  headers.append('Authorization', 'Basic ' + base64.encode(username + ":" +  password));
    headers.append('Origin','http://localhost:3000');

  const handleSubmit = async (e) => {
    e.preventDefault();
    alert(`The response was: ${response}`);

    try {
      const app_response = await fetch('http://localhost:5000/api/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ response }),
      });

      if (app_response.ok) {
        const reply = await response.json();
        setResult(reply);
      } else {
        throw new Error('Request failed with status ' + app_response.status);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={response} onChange={(e) => setResponse(e.target.value)} />
        <button type="submit">Submit</button>
      </form>
      {result && <div>{JSON.stringify(result)}</div>}
    </div>
  );
}

export default App;
